import logo from './logo.svg';
import './App.css';
import React from 'react';
import Pokemon, {IPokemon} from "./components/pokemon/pokemon";

interface IProps {
}

interface IState {
    pokemon: IPokemon
}

class App extends React.Component<IProps, IState> {
    constructor(props: IProps) {
        super(props)
        this.state = {
            pokemon: {id: Number.NaN, imageURL: "", name: ""}
        }
        this.handleInputChanges = this.handleInputChanges.bind(this)
    }

    handleInputChanges(event: any) {
        this.setState(() => this.state.pokemon.name = event.target.value)
    }

    handleNewSearch() {

        console.log(this.state)
        if (this.state && this.state.pokemon && this.state.pokemon.name) {
            fetch(`http://localhost:9000/api/pokemon/${this.state.pokemon.name}`)
                .then(response => response.json())
                .then(data => this.setState({
                    pokemon: data
                }))
        }
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                </header>
                <div>
                    <input type="text" value={this.state.pokemon.name} placeholder="Write a Pokemon name"
                           onChange={this.handleInputChanges}/>
                    <button onClick={this.handleNewSearch}>Search</button>
                </div>
                <Pokemon pokemon={this.state.pokemon}/>
            </div>
        );
    }
}

export default App;
