import logo from './logo.svg';
import './App.css';
import React from 'react';
import Pokemon, {IPokemon} from "./components/pokemon/pokemon";

interface IProps {
}

interface IState {
    name: string,
    id: Number,
    imageURL: string
}

class App extends React.Component<IProps, IState> {
    constructor(props: IProps) {
        super(props)
        this.state = {
            name: '',
            id: 0,
            imageURL: ''
        }
        this.handleInputChanges = this.handleInputChanges.bind(this)
        this.handleNewSearch = this.handleNewSearch.bind(this)
    }

    handleInputChanges(event: any) {
        this.setState({
            id: Number.NaN, imageURL: "", name: event.target.value}
            )
    }

    handleNewSearch() {

            fetch(`http://localhost:3000/api/pokemon/${this.state.name}`)
                .then(response => response.json())
                .then(data => this.setState({
                    name: data.name,
                    id: data.id,
                    imageURL: data.imageURL
                }))
        }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                </header>
                <div>
                    <input type="text" value={this.state.name} placeholder="Write a Pokemon name"
                           onChange={this.handleInputChanges}/>
                    <button onClick={this.handleNewSearch}>Search</button>
                </div>
                <Pokemon name={this.state.name} id={this.state.id} imageURL={this.state.imageURL}/>
            </div>
        );
    }
}

export default App;
