import logo from './logo.svg';
import './App.css';
import React from 'react';
import Pokemon, {IPokemon} from "./components/pokemon/pokemon";
import Header from "./components/header/header";
import { Form } from 'react-bootstrap';


interface IProps {
}

interface IState {
    name: string,
    id: Number,
    imageURL: string,
    searchDone: boolean,
    isLoading: boolean,
}

class App extends React.Component<IProps, IState> {
    constructor(props: IProps) {
        super(props)
        this.state = {
            name: '',
            id: 0,
            imageURL: '',
            searchDone: false,
            isLoading: false

        }
        this.handleInputChanges = this.handleInputChanges.bind(this)
        this.handleNewSearch = this.handleNewSearch.bind(this)
    }

    handleInputChanges(event: any) {
        this.setState({
                id: Number.NaN, imageURL: "", name: event.target.value, searchDone: false
            }
        )
    }

    handleNewSearch() {
        this.setState({isLoading : true})
        fetch(`http://localhost:9000/api/pokemon/${this.state.name}`)
            .then(response => response.json())
            .then(data => this.setState({
                name: data.name,
                id: data.id,
                imageURL: data.imageURL,
                searchDone: true,
                isLoading: false
            }))
    }

    render() {
        return (
            <div className="App">
                <Header />
                <div className="parent">
                    <div>
                        <input type="text" value={this.state.name} placeholder="Write a Pokemon name"
                               onChange={this.handleInputChanges}/>
                        <button onClick={this.handleNewSearch}>Search</button>

                        <Form>
                            <Form.Group controlId="searchCtrl">
                                <Form.Label>Find a Pokemon</Form.Label>
                                <Form.Control type="text" placeholder="Write a Pokemon name" />
                                <Form.Text className="text-muted">
                                    *You can also write an ID
                                </Form.Text>
                            </Form.Group>
                        </Form>
                    </div>
                    { this.state.searchDone ? this.state.isLoading ? <p>Loading...</p> : <Pokemon name={this.state.name} id={this.state.id} imageURL={this.state.imageURL}/> : <div></div>}
                </div>
            </div>
        );
    }
}

export default App;
