import logo from './logo.svg';
import './App.css';
import React from 'react';
import Pokemon, {IPokemon} from "./components/pokemon/pokemon";

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            pokemon: undefined
        }
        this.handleNewSearch = this.handleNewSearch.bind(this)
    }

    handleNewSearch(event) {
        fetch(`http://localhost:9000/api/pokemon/${event.target.value}`)
            .then(response => response.json())
            .then(data => this.setState({
                pokemon: data
            }))
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                </header>
                <input type="text" placeholder="Write a Pokemon name" onChange={this.handleNewSearch}/>
                <Pokemon name={this.state.pokemon.name} imageURL={this.state.pokemon.imageURL}/>
            </div>
        );
    }
}

export default App;
